CREATE TEMPORARY TABLE Device$sensorTypeData USING CarbonAnalytics OPTIONS(tableName "ORG_WSO2_IOT_DEVICES_SENSORS_$sensorType");
CREATE TEMPORARY TABLE Device$sensorTypeSummaryData USING CarbonAnalytics OPTIONS (tableName "DEVICE_$sensorType_SUMMARY", schema "sensor_value DOUBLE, deviceType STRING -i, deviceId STRING -i, owner STRING -i, time_stamp LONG -i",primaryKeys "time_stamp", incrementalProcessing "Device$sensorTypeSummaryData, 60, 2", mergeSchema "false");
insert INTO table Device$sensorTypeSummaryData select sensor_value, meta_deviceType as deviceType, meta_deviceId as deviceId, meta_owner as owner, meta_time*1000 as time_stamp from Device$sensorTypeData group by sensor_value, meta_deviceType, meta_deviceId, meta_owner, meta_time;
CREATE TEMPORARY TABLE $sensorTypeServiceStatPerSecond USING CarbonAnalytics OPTIONS (tableName "Service-$sensorType-Stat-Per-Second", schema "year INT -i, month INT -i, day INT -i, hour INT -I, minute INT -i, second INT -i, total DOUBLE -i, minValue DOUBLE -i, maxValue DOUBLE -i, noOfInvocation INT -I, time LONG -i, deviceId STRING -i", primaryKeys "year, month, day, hour, minute, second, deviceId", mergeSchema "false");
INSERT INTO TABLE $sensorTypeServiceStatPerSecond SELECT year, month, day, hour, minute, second,total, minValue, maxValue, noOfInvocation, time, deviceId FROM (SELECT temp.year, temp.month, temp.day, temp.hour, temp.minute, temp.second, sum(temp.sensor_value) as total, min(temp.sensor_value) as minValue, max(temp.sensor_value) as maxValue, count(*) as noOfInvocation, getSecondStartingTime(year, month, day, hour, minute, second) as time, temp.deviceId FROM (SELECT getYear(time_stamp) as year, getMonth(time_stamp) as month, getDay(time_stamp) as day, getHour(time_stamp) as hour, getMinute(time_stamp) as minute, getSeconds(time_stamp) as second, sensor_value , deviceId, owner FROM Device$sensorTypeSummaryData) temp GROUP BY temp.year, temp.month, temp.day, temp.hour, temp.minute, temp.second, temp.deviceId) result;
CREATE TEMPORARY TABLE $sensorTypeServiceStatPerSecond USING CarbonAnalytics OPTIONS (tableName "Service-$sensorType-Stat-Per-Second", incrementalProcessing "$sensorTypeServiceStatPerSecond, 60, 2");
CREATE TEMPORARY TABLE $sensorTypeServiceStatPerMinute USING CarbonAnalytics OPTIONS (tableName "Service-$sensorType-Stat-Per-Minute", schema "year INT -i, month INT -i, day INT -i, hour INT -I, minute INT -i, total DOUBLE -i, minValue DOUBLE -i, maxValue DOUBLE -i, noOfInvocation INT -I, time LONG -i, deviceId STRING -i", primaryKeys "year, month, day, hour, minute, deviceId", mergeSchema "false");
INSERT INTO TABLE $sensorTypeServiceStatPerMinute SELECT year, month, day, hour, minute, sum(total) as total, min(minValue) as minValue, max(maxValue) as maxValue, count(*) as noOfInvocation, getMinuteStartingTime(year, month, day, hour, minute) as time, deviceId FROM $sensorTypeServiceStatPerSecond GROUP BY year, month, day, hour, minute, deviceId ORDER BY year, month, day, hour, minute ASC;
CREATE TEMPORARY TABLE $sensorTypeServiceStatPerMinute USING CarbonAnalytics OPTIONS (tableName "Service-$sensorType-Stat-Per-Minute", incrementalProcessing "$sensorTypeServiceStatPerMinute, 3600");
CREATE TEMPORARY TABLE $sensorTypeServiceStatPerHour USING CarbonAnalytics OPTIONS (tableName "Service-$sensorType-Stat-Per-Hour", schema "year INT -i, month INT -i, day INT -i, hour INT -I, total DOUBLE -i, minValue DOUBLE -i, maxValue DOUBLE -i, noOfInvocation INT -I, time LONG -i, deviceId STRING -i", primaryKeys "year, month, day, hour, deviceId", mergeSchema "false");
INSERT INTO TABLE $sensorTypeServiceStatPerHour SELECT year, month, day, hour, sum(total) as total, min(minValue) as minValue, max(maxValue) as maxValue, count(*) as noOfInvocation, getHourStartingTime(year, month, day, hour) as time, deviceId FROM $sensorTypeServiceStatPerMinute GROUP BY year, month, day, hour, deviceId ORDER BY year, month, day, hour ASC;
CREATE TEMPORARY TABLE $sensorTypeServiceStatPerHour USING CarbonAnalytics OPTIONS (tableName "Service-$sensorType-Stat-Per-Hour", incrementalProcessing "$sensorTypeServiceStatPerHour, 86400");
CREATE TEMPORARY TABLE $sensorTypeServiceStatPerDay USING CarbonAnalytics OPTIONS (tableName "Service-$sensorType-Stat-Per-Day", schema "year INT -i, month INT -i, day INT -i, total DOUBLE -i, minValue DOUBLE -i, maxValue DOUBLE -i, noOfInvocation INT -I, time LONG -i, deviceId STRING -i", primaryKeys "year, month, day, deviceId", mergeSchema "false");
INSERT INTO TABLE $sensorTypeServiceStatPerDay SELECT year, month, day, sum(total) as total, min(minValue) as minValue, max(maxValue) as maxValue, count(*) as noOfInvocation, getDateStartingTime(year, month, day) as time, deviceId FROM $sensorTypeServiceStatPerHour GROUP BY year, month, day, deviceId ORDER BY year, month, day ASC;
CREATE TEMPORARY TABLE $sensorTypeServiceStatPerDay USING CarbonAnalytics OPTIONS (tableName "Service-$sensorType-Stat-Per-Day", incrementalProcessing "$sensorTypeServiceStatPerDay, 604800");
CREATE TEMPORARY TABLE $sensorTypeServiceStatPerMonth USING CarbonAnalytics OPTIONS (tableName "Service-$sensorType-Stat-Per-Month", schema "year INT -i, month INT -i, total DOUBLE -i, minValue DOUBLE -i, maxValue DOUBLE -i, noOfInvocation INT -I, time LONG -i, deviceId STRING -i", primaryKeys "year, month, deviceId", mergeSchema "false");
INSERT INTO TABLE $sensorTypeServiceStatPerMonth SELECT year, month, sum(total) as total, min(minValue) as minValue, max(maxValue) as maxValue, count(*) as noOfInvocation, getMonthStartingTime(year, month) as time, deviceId FROM $sensorTypeServiceStatPerDay GROUP BY year, month, deviceId ORDER BY year, month ASC;

