templates.template:

/* Enter a unique ExecutionPlan */
@Plan:name('org.wso2.iot.devices.sensors.$sensorType.statuspersecond')

/* Enter a unique description for ExecutionPlan */
-- @Plan:description('Execution plain which used for mapping sensor input stream into per-second summarization stream')

/* define streams/tables and write queries here ... */

@Import('org.wso2.iot.devices.sensors.$sensorType:1.0.0')
define stream $sensorTypePerSecondStreamMapper (meta_owner string, meta_deviceType string, meta_deviceId string, meta_time long, sensor_value double);

@Export('org.wso2.iot.devices.sensors.$sensorType.presecond.statistics:1.0.0')
define stream $sensorTypePerSecondStats (meta_owner string, meta_deviceType string, meta_deviceId string, meta_time long, noOfInvocation long, total double, average double, maxValue double, minValue double, year int, month int, day int, hour int, minute int, second int);

from $sensorTypePerSecondStreamMapper#window.timeBatch(1 sec)
select meta_owner, meta_deviceType, meta_deviceId, meta_time, count(*) as noOfInvocation, sum(sensor_value) as total,
avg(sensor_value) as average, max(sensor_value) as maxValue, min(sensor_value) as minValue, time:extract(meta_time, 'year') as year, time:extract(meta_time, 'month') as month, time:extract(meta_time, 'day') as day,
time:extract(meta_time, 'hour') as hour, time:extract(meta_time, 'minute') as minute, time:extract(meta_time, 'second') as second
GROUP BY meta_owner, meta_deviceType, meta_deviceId, meta_time
insert into $sensorTypePerSecondStats;


/* Enter a unique ExecutionPlan */
@Plan:name('org.wso2.iot.devices.sensors.$sensorType.statusperminute')

/* Enter a unique description for ExecutionPlan */
-- @Plan:description('Execution plain which used for mapping per-second summarized input stream into per-minute summarization stream')

/* define streams/tables and write queries here ... */

@Import('org.wso2.iot.devices.sensors.$sensorType.presecond.statistics:1.0.0')
define stream $sensorTypePerMinuteStreamMapper (meta_owner string, meta_deviceType string, meta_deviceId string, meta_time long, noOfInvocation long, total double, average double, maxValue double, minValue double, year int, month int, day int, hour int, minute int, second int);

@Export('org.wso2.iot.devices.sensors.$sensorType.preminute.statistics:1.0.0')
define stream $sensorTypePerMinuteStats (meta_owner string, meta_deviceType string, meta_deviceId string,meta_time long, noOfInvocation long, total double, average double, maxValue double, minValue double, year int, month int, day int, hour int, minute int);

from $sensorTypePerMinuteStreamMapper#window.timeBatch(1 min)
select meta_owner, meta_deviceType, meta_deviceId, meta_time, sum(noOfInvocation) as noOfInvocation, sum(total) as total,
avg(average) as average, max(maxValue) as maxValue, min(minValue) as minValue, time:extract(meta_time, 'year') as year, time:extract(meta_time, 'month') as month, time:extract(meta_time, 'day') as day,
time:extract(meta_time, 'hour') as hour, time:extract(meta_time, 'minute') as minute
GROUP BY meta_owner, meta_deviceType, meta_deviceId, year, month, day, hour, minute
insert into $sensorTypePerMinuteStats;

executionParameters.sparkScript:
CREATE TEMPORARY TABLE Device$sensorTypeData USING CarbonAnalytics OPTIONS(tableName "ORG_WSO2_IOT_DEVICES_SENSORS_$sensorType_PREMINUTE_STATISTICS");
CREATE TEMPORARY TABLE Device$sensorTypeSummaryData USING CarbonAnalytics OPTIONS (tableName "Device_$sensorType_Summary", schema "meta_owner string -i, meta_deviceType string -i, meta_deviceId string -i, time long -i,year INT -i, month INT -i, day INT -i, hour INT -I,minute INT -I , noOfInvocation long -i, total double -i, maxValue double -i, minValue double -i");
INSERT INTO table Device$sensorTypeSummaryData select meta_owner, meta_deviceType , meta_deviceId as deviceId, meta_time as time ,year, month, day, hour,minute , noOfInvocation, total, maxValue, minValue from Device$sensorTypeData GROUP BY meta_deviceType, meta_deviceId, meta_owner, meta_time,year, month, day, hour ,minute, noOfInvocation, total, maxValue, minValue ORDER BY year, month, day, hour, minute ASC;
CREATE TEMPORARY TABLE $sensorTypeServiceStatPerHour USING CarbonAnalytics OPTIONS (tableName "Service-$sensorType-Stat-Per-Hour", schema "year INT -i, month INT -i, day INT -i, hour INT -I, total DOUBLE -i, minValue DOUBLE -i, maxValue DOUBLE -i, noOfInvocation INT -I, time LONG -i, deviceId STRING -i,owner string -i, deviceType string -i", primaryKeys "year, month, day, hour, deviceId, deviceType", mergeSchema "false");
INSERT INTO TABLE $sensorTypeServiceStatPerHour SELECT year, month, day, hour,total, minValue, maxValue, noOfInvocation, time, meta_deviceId, meta_owner, meta_deviceType FROM (SELECT temp.year, temp.month, temp.day, temp.hour, sum(temp.total) as total, min(temp.minValue) as minValue, max(temp.maxValue) as maxValue, sum(temp.noOfInvocation) as noOfInvocation, getHourStartingTime(year, month, day, hour) as time, temp.meta_deviceId, temp.meta_owner, temp.meta_deviceType FROM (SELECT getYear(time) as year, getMonth(time) as month, getDay(time) as day, getHour(time) as hour, meta_deviceId, meta_owner,meta_deviceType, total, maxValue, minValue, noOfInvocation FROM Device$sensorTypeSummaryData) temp GROUP BY temp.year, temp.month, temp.day, temp.hour, temp.meta_deviceId, temp.meta_deviceType, temp.meta_owner) result;
CREATE TEMPORARY TABLE $sensorTypeServiceStatPerHour USING CarbonAnalytics OPTIONS (tableName "Service-$sensorType-Stat-Per-Hour", incrementalProcessing "$sensorTypeServiceStatPerHour, 86400");
CREATE TEMPORARY TABLE $sensorTypeServiceStatPerDay USING CarbonAnalytics OPTIONS (tableName "Service-$sensorType-Stat-Per-Day", schema "year INT -i, month INT -i, day INT -i, total DOUBLE -i, minValue DOUBLE -i, maxValue DOUBLE -i, noOfInvocation INT -I, time LONG -i, deviceId STRING -i,owner string -i, deviceType string -i", primaryKeys "year, month, day, deviceId, deviceType", mergeSchema "false");
INSERT INTO TABLE $sensorTypeServiceStatPerDay SELECT year, month, day, sum(total) as total, min(minValue) as minValue, max(maxValue) as maxValue, sum(noOfInvocation) as noOfInvocation, getDateStartingTime(year, month, day) as time, deviceId, owner, deviceType FROM $sensorTypeServiceStatPerHour GROUP BY year, month, day, deviceId, owner, deviceType ORDER BY year, month, day ASC;
CREATE TEMPORARY TABLE $sensorTypeServiceStatPerDay USING CarbonAnalytics OPTIONS (tableName "Service-$sensorType-Stat-Per-Day", incrementalProcessing "$sensorTypeServiceStatPerDay, 604800");
CREATE TEMPORARY TABLE $sensorTypeServiceStatPerMonth USING CarbonAnalytics OPTIONS (tableName "Service-$sensorType-Stat-Per-Month", schema "year INT -i, month INT -i, total DOUBLE -i, minValue DOUBLE -i, maxValue DOUBLE -i, noOfInvocation INT -I, time LONG -i, deviceId STRING -i,owner string -i, deviceType string -i", primaryKeys "year, month, deviceId", mergeSchema "false");
INSERT INTO TABLE $sensorTypeServiceStatPerMonth SELECT year, month, sum(total) as total, min(minValue) as minValue, max(maxValue) as maxValue, sum(noOfInvocation) as noOfInvocation, getMonthStartingTime(year, month) as time, deviceId, owner, deviceType FROM $sensorTypeServiceStatPerDay GROUP BY year, month, deviceId, owner, deviceType ORDER BY year, month ASC;

