 var conf;
 var drawGadget;
 var refreshIntervalId;

 $(function () {
 var gadgetLocation = '/portal/store/carbon.super/fs/gadget/$sensorType-historical-value-chart-min';
     var schema;
     var pref = new gadgets.Prefs();

     var refreshInterval;
     var providerData;

     var CHART_CONF = 'chart-conf';
     var PROVIDER_CONF = 'provider-conf';

     var REFRESH_INTERVAL = 'refreshInterval';

 var init = function () {
     $.ajax({
         url: gadgetLocation + '/conf.json',
         method: "GET",
         contentType: "application/json",
         async: false,
         success: function (data) {
             conf = JSON.parse(data);
             $.ajax({
                 url: gadgetLocation + '/gadget-controller.jag?action=getSchema',
                 method: "POST",
                 data: JSON.stringify(conf),
                 contentType: "application/json",
                 async: false,
                 success: function (data) {
                     schema = data;
                 }
             });

             getProviderData();

            }
        });
 }

 var getProviderData = function (){
     $.ajax({
         url: gadgetLocation + '/gadget-controller.jag?action=getData',
         method: "POST",
         data: JSON.stringify(conf),
         contentType: "application/json",
         async: false,
         success: function (data) {
         providerData = data;
         }
     });
 }


     drawGadget = function (){

     draw('#canvas', conf[CHART_CONF], schema, providerData);
     refreshIntervalId = setInterval(function() {
         update(getProviderData);
     },pref.getInt(REFRESH_INTERVAL));

 }
     init();
     drawGadget();
 });

 gadgets.HubSettings.onConnect = function () {
     gadgets.Hub.subscribe("subscriber", function (topic, data, subscriber) {
         console.log("-conf--"+ conf["provider-conf"]["time-from"]);
         conf["provider-conf"]["time-from"] = data.timeFrom;
         conf["provider-conf"]["time-to"] =  data.timeTo;
         if(refreshIntervalId) {
             clearInterval(refreshIntervalId);
         }
         if(drawGadget){
             drawGadget();
         }
     });
 };


